package day6

const val LARGE = ""

fun main() {
    println("Start of packet: ${findStartingIndex(LARGE, 4)}")
    println("Start of message: ${findStartingIndex(LARGE, 14)}")
}

fun findStartingIndex(data: String, markerLength: Int): Int {
    return data.windowedSequence(markerLength).indexOfFirst {
        it.toCharArray().toSet().size == markerLength
    } + markerLength
}